workflow.info('Logging INFO from inside a script - begin');

provisionCatalogItemType();

function provisionCatalogItemType() {
	var utils = new x_moda_morpheus_ca.Utils();

	activity.result = 'success';

	var grItem = new GlideRecord('sc_req_item');
	if(grItem.get(current.sys_id)){

		var scratchpad = {};

		for (var item in workflow.inputs) {
			utils.log("info", "input: item=" + item + ", value=" + workflow.inputs[item]);
			scratchpad[item] = workflow.inputs[item];
		}

		scratchpad.u_req_item_id = current.sys_id;

		workflow.info('u_req_item_id' + scratchpad.u_req_item_id);

		var lookupUtils = new x_moda_morpheus_ca.LookUpUtils();
		var catUtils = new x_moda_morpheus_ca.CatalogItemTypeUtils();
		var companyId = gs.getUser().getCompanyID();

		var authConfig = lookupUtils.fetchAuthConfig(undefined, companyId);
		scratchpad.authConfig = authConfig;
		scratchpad.access_token = authConfig.access_token;

		if(scratchpad.access_token == '' || typeof(scratchpad.access_token) == 'undefined') {
			_handleError('Error connecting to Morpheus.  Please verify connection information.');
		} else {
			utils.log("info", 'in provisionCatalogItemType with scratchpad: ' + JSON.stringify(scratchpad));

			var grItem = new GlideRecord('sc_req_item');
			if(grItem.get(scratchpad.u_req_item_id)){

				var grCatItem = new GlideRecord('sc_cat_item');
				if(grCatItem.get(grItem.cat_item)){

					// Build up the request to Morpheus
					var payload = {item: {type: {id: parseInt(scratchpad.external_id)}, config: {integrationData: {type: 'snow', ritm: current.sys_id.toString()}}}};

					// Add all the configuration options
					for(var key in scratchpad) {
						var val = scratchpad[key];
						if(catUtils.isMorpheusOptionType(key)) {
							var fieldInfo = catUtils.getFieldInfoForMorpheus(key, val);
							payload.item.config[fieldInfo.fieldName] = fieldInfo.fieldValue;

							// Make sure to remove the expires field on any gr so we don't delete an option that was actually used to provision something
							if(fieldInfo.optionSysIds.length > 0) {
								for(var i = 0; i < fieldInfo.optionSysIds.length; i++) {
									var gr = new GlideRecord('x_moda_morpheus_ca_options');
									if(gr.get(fieldInfo.optionSysIds[i])){
										gr.expires = "NULL";
										gr.update();
									}
								}
							}
						}
					}

					var parser = new global.JSON();
					var encodedPayload = parser.encode(payload);

					utils.log("info", 'calling Morpheus to create catalog item with payload:'+ encodedPayload);
					var createResultBody = utils.callMorpheus('x_moda_morpheus_ca.Morpheus Catalog Item Type Order', scratchpad.authConfig, {}, 'post', encodedPayload);
					utils.log("info", 'results calling Morpheus ' + JSON.stringify(createResultBody));

					if(createResultBody.success == false) {
						var errorString = 'Error occurred with call to Morpheus: ';
						for (var err in createResultBody.errors) {
							errorString += err + '=' + createResultBody.errors[err] + ' ';
						}
						_handleError(errorString);
					} else {
						utils.log("info", "Successfully created catalog item type in Morpheus with order id " + createResultBody.order.id);
						grItem.comments = "Successfully created catalog item";
						if(createResultBody.order.stats) {
							grItem.price = createResultBody.order.stats.price;
						}
						grItem.update();
					}
				}
			}
		}
	}
}

function _handleError(msg){
	var utils = new x_moda_morpheus_ca.Utils();
	activity.result = 'error';
	activity.fault_description = msg;
	utils.log("info", "handlerror " + msg);

	workflow.error('Morpheus - Provision Catalog Item Type: ' + msg);
}

