workflow.info('Logging INFO from inside a script - begin');

provisionAppInstance();

function provisionAppInstance() {
	var utils = new x_moda_morpheus_ca.Utils();

	activity.result = 'success';

	var grItem = new GlideRecord('sc_req_item');
	if(grItem.get(current.sys_id)) {

		var quantity = grItem.quantity;

		var cnt = 0;
		for(cnt = 0; cnt < quantity; cnt++) {

			var scratchpad = {};

			for (var item in workflow.inputs) {
				scratchpad[item] = workflow.inputs[item];
			}

			scratchpad.u_req_item_id = current.sys_id;

			workflow.info('u_req_item_id' + scratchpad.u_req_item_id);

			var lookupUtils = new x_moda_morpheus_ca.LookUpUtils();
			var user = new GlideRecord('sys_user');
			user.get(gs.getUserID());
			var domainSysId = user.sys_domain;
			var companySysId = user.company.sys_id;

			var authConfig = lookupUtils.fetchAuthConfig(undefined, companySysId, domainSysId);
			scratchpad.authConfig = authConfig;
			scratchpad.access_token = authConfig.access_token;
			var tenantId = authConfig.tenantId;


			if(scratchpad.access_token == '' || typeof(scratchpad.access_token) == 'undefined') {
				_handleError('Error connecting to Morpheus.  Please verify connection information.');
			} else {
				utils.log("info", 'in provisionAppInstance with scratchpad: ' + scratchpad);

				var grItem = new GlideRecord('sc_req_item');
				if(grItem.get(scratchpad.u_req_item_id)){

					var grCatItem = new GlideRecord('sc_cat_item');
					if(grCatItem.get(grItem.cat_item)){

						gs.debug('here:' + scratchpad.appName);

						var appName = scratchpad.appName;
						if(cnt > 0) {
							appName = scratchpad.appName + "-" + cnt;
						}

						// Call morpheus to fetch the current blueprint definition
						var createResultBody = utils.callMorpheus('x_moda_morpheus_ca.Morpheus Blueprint', scratchpad.authConfig, {id: parseInt(scratchpad.external_id)});
						if(createResultBody.success == false) {
							var errorString = 'Error occurred with call to Morpheus: ';
							for (var err in createResultBody.errors) {
								errorString += err + '=' + createResultBody.errors[err] + ' ';
							}
							_handleError(errorString);
						} else {
							utils.log("info", "createResultBody: " + JSON.stringify(createResultBody));
							var blueprintConfig = createResultBody.blueprint.config;

							// Build up the request to Morpheus
							var payload = blueprintConfig;
							payload.name = appName.toString();
							payload.id = parseInt(scratchpad.external_id);
							payload.templateName = scratchpad.external_name.toString();
							payload.defaultCloud = { id: parseInt(scratchpad.cloud_id), name: scratchpad.cloud_name.toString() };
							payload.group = { id: parseInt(scratchpad.group_id), name: scratchpad.group_name.toString() };
							if(scratchpad.environment && scratchpad.environment.toString() != '') {
								payload.environment = scratchpad.environment.toString();
							}

							// for(j = 0; j < payload.tiers.App.instances.length; j++) {
							// 	var currentInstance = payload.tiers.App.instances[j];
							// 	delete currentInstance['groups']
							// }

							var parser = new global.JSON();
							var encodedPayload = parser.encode(payload);

							utils.log("info", 'calling Morpheus to validate app with payload:'+ encodedPayload);
							var validateResultBody = utils.callMorpheus('x_moda_morpheus_ca.Morpheus App Validate', scratchpad.authConfig, {}, 'post', encodedPayload);
							if(validateResultBody.success == false) {
								var errorString = 'The desired app is not valid: ' + JSON.stringify(validateResultBody);
								_handleError(errorString);
							} else {
								utils.log("info", 'calling Morpheus to create app with payload:'+ encodedPayload);
								var createResultBody = utils.callMorpheus('x_moda_morpheus_ca.Morpheus App', scratchpad.authConfig, {}, 'post', encodedPayload);
								utils.log("info", 'results calling Morpheus ' + JSON.stringify(createResultBody));

								if(createResultBody.success == false) {
									var errorString = 'Error occurred with call to Morpheus: ';
									for (var err in createResultBody.errors) {
										errorString += err + '=' + createResultBody.errors[err] + ' ';
									}
									_handleError(errorString);
								} else {
									utils.log("info", "Successfully created app in Morpheus with id " + createResultBody.app.id);
									grItem.comments = "Successfully created app: " + gs.getProperty('x_moda_morpheus_ca.morpheus_url') + "/provisioning/apps/" + createResultBody.app.id;
									grItem.update();
								}
							}
						}
					}
				}
			}
		}
	}
}

function _handleError(msg){
	activity.result = 'error';
	activity.fault_description = msg;
	gs.info("handlerror " + msg);

	workflow.error('Morpheus - Provision App: ' + msg);
}

